#include <bits/stdc++.h>

using namespace std;


class PackageSizes {
public:
int MAX = 1000000;
int getMinimum(vector <int> v, int total) {
	int n = v.size();
	vector<int> best(1001, MAX);
	best[0] = 0;
	for (int t = 1; t <= total; t++) {
		for (int i = 0; i < n; i++) {
			if (v[i] <= t) {
				best[t] = min(best[t], 1+best[t-v[i]]);
			}
		}
	}
	return best[total] == MAX ? -1 : best[total];
}
};


